    }

    // -------------------------  EXTERNAL, MUTATING  ------------------------------

    function addUniV3Pools(IUniswapV3Pool[] memory pools) external {
        require(isAdmin(msg.sender), ExceptionsLibrary.FORBIDDEN);
        _addUniV3Pools(pools);
    }

