library StringUtils {
    function utfLength(bytes1 b) private pure returns (uint8) {
        if (b < 0x80) {
            return 1;
        } else if(b < 0xE0) {
            return 2;
        } else if(b < 0xF0) {
            return 3;
        } else if(b < 0xF8) {
            return 4;
        } else if(b < 0xFC) {
            return 5;
        } else {
            return 6;
        }
    }

    function firstN(bytes memory arr, uint256 n) internal pure returns (bytes memory) {
        bytes memory newArray = new bytes(n);
        for (uint256 i = 0; i < n; i++) {
            newArray[i] = arr[i];
        }

        return newArray;
    }

    function insertAsciiBefore(bytes memory str, bytes1 target, bytes1 insert) internal pure returns (bytes memory) {
        require(utfLength(target) == 1, "StringUtils: target must be ASCII");
        require(utfLength(insert) == 1, "StringUtils: insert must be ASCII");

        bytes memory newString = new bytes(str.length * 2);
        uint256 from = 0;
        uint256 to = 0;
        while (from < str.length) {
            // console.log("Test %s %s", from, uint8(str[from]));
            // console.log("Index %s, char %s", from, copy);
            bytes1 b = str[from];
            uint8 bLength = utfLength(b);

            if (bLength == 1 && b == target) {
                // ASCII character that matches our target
                newString[to] = insert;
                to++;
            }

            for (uint8 j = 0; j < bLength; j++) {
                newString[to + j] = str[from + j];
            }

            to += bLength;
            from += bLength;
        }

        return firstN(newString, to);
    }

    function insertAsciiBeforeString(string memory str, bytes1 target, bytes1 insert) internal pure returns (string memory) {
        return string(insertAsciiBefore(bytes(str), target, insert));
    }
}

contract ZangNFT is
    ERC1155OnChain,
    IERC1155MetadataURI,
    ERC2981PerTokenRoyalties
{
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    mapping(uint256 => string) private _textURIs;

    mapping(uint256 => string) private _names;

    mapping(uint256 => string) private _descriptions;

    mapping(uint256 => address) private _authors;

    constructor() ERC1155OnChain("ZangNFT", "ZNG") {}

    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC1155OnChain, ERC2981PerTokenRoyalties, IERC165)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    function lastTokenId() public view returns (uint256) {
        return _tokenIds.current();
    }

    function exists(uint256 _tokenId) public view returns (bool) {
        if(_tokenId == 0) return false;
        return lastTokenId() >= _tokenId;
    }

    function uri(uint256 tokenId) public view override returns (string memory) {
        require(exists(tokenId), "ZangNFT: uri query for nonexistent token");
        string memory json = Base64.encode(
            bytes(
                string(
                    abi.encodePacked(
                        '{ "name": "',
                        StringUtils.insertAsciiBeforeString(_names[tokenId], '"', '\\'),
                        '", ',
                        '"description" : ',
                        '"',
                        StringUtils.insertAsciiBeforeString(_descriptions[tokenId], '"', '\\'),
                        '", ',
                        //'"image": "data:image/svg+xml;base64,', Base64.encode(bytes(output)), '", '
                        '"textURI" : ',
                        '"',
                        textURI(tokenId),
                        '"',
                        "}"
                    )
                )
            )
        );
        string memory output = string(
            abi.encodePacked("data:application/json;base64,", json)
        );

        return output;
    }

    function authorOf(uint256 _tokenId) public view returns (address) {
        address author = _authors[_tokenId];
        require(
            author != address(0),
            "ZangNFT: author query for nonexistent token"
        );
        return author;
    }

    function mint(
        string memory textURI_,
        string memory name_,
        string memory description_,
        uint256 amount_,
        uint256 royaltyPercentage_, //NB: two decimals, so 10% is 1000
        address royaltyRecipient_,
        bytes memory data_
    ) public returns (uint256) {
        _tokenIds.increment();

        uint256 newTokenId = _tokenIds.current();
        _setTextURI(newTokenId, textURI_);
        _names[newTokenId] = name_;
        _descriptions[newTokenId] = description_;
        _authors[newTokenId] = msg.sender;
        _setTokenRoyalty(newTokenId, royaltyRecipient_, royaltyPercentage_);

        _mint(msg.sender, newTokenId, amount_, data_);

        return newTokenId;
    }

    function _setTextURI(uint256 _tokenId, string memory _textURI) internal {
        _textURIs[_tokenId] = _textURI;
    }

    function textURI(uint256 tokenId)
        public
        view
        virtual
        returns (string memory)
    {
        require(
            exists(tokenId),
            "ZangNFT: textURI query for nonexistent token"
        );
        return _textURIs[tokenId];
    }
}
